general.global_css:
@keyframes wpgb-underline-out {
	0% {
		transform: scaleX(0);
		transform-origin: left;
	}
	100% {
		transform: scaleX(1);
		transform-origin: left;
	}
}
@keyframes wpgb-underline-in {
	0% {
		transform: scaleX(1);
		transform-origin: right;
	}
	100% {
		transform: scaleX(0);
		transform-origin: right;
	}
}

idle.custom_css_after:
content: '';
position: absolute;
display: block;
height: 2px;
width: 100%;
left: 0;
bottom: -4px;
background: #fff;
opacity: .8;
pointer-events: none;
animation: wpgb-underline-in .45s cubic-bezier(.15,.75,.5,1) forwards;

box-sizing: border-box;
content: '';
width: calc(100% - 2em);
height: calc(100% - 2em);
top: 1em;
left: 1em;
position: absolute;
border: .325em solid white;

idle.custom_css_before:
content: '•';
display: inline-block;
width: 1.5em;
text-align: center;
